import { useSelector } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faIndianRupeeSign } from "@fortawesome/free-solid-svg-icons";

import Card from "../UI/Card";
import classes from "./Cart.module.css";
import CartItem from "./CartItem";

const Cart = (props) => {
  const cartItems = useSelector((state) => state.cart.items);

  const totalPrice = cartItems.reduce(
    (acc, { totalPrice, totalActualPrice }) => {
      acc.totalPrice + totalPrice;
      acc.totalActualPrice + totalActualPrice;
      return acc;
    },
    { totalPrice: 0, totalActualPrice: 0 }
  );

  const totalActualPrice = cartItems
    .reduce((acc, { totalActualPrice }) => {
      return acc + totalActualPrice;
    }, 0)
    .toFixed(2);

  const discount = totalActualPrice - totalPrice;

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <div>
        {cartItems.map((item) => (
          <CartItem
            key={item.id}
            item={{
              id: item.id,
              title: item.name,
              quantity: item.quantity,
              total: item.totalPrice,
              price: item.price,
              actualPrice: item.actualPrice,
            }}
          />
        ))}
      </div>
      <div className='red-color'>
        Total <FontAwesomeIcon icon={faIndianRupeeSign} /> {totalPrice}
        <span className={classes.itemprice}>
          {" "}
          ( <FontAwesomeIcon icon={faIndianRupeeSign} />
          {discount} discount)
        </span>
      </div>
    </Card>
  );
};

export default Cart;
