import { Fragment } from "react";
import ProductItem from "./ProductItem";
import classes from "./Products.module.css";
import utility from "../../utility/utility";

const DUMMY_PRODUCTS = [
  {
    shoes: [
      {
        id: "p1",
        price: 6,
        title: "First Shoe",
        description: "I use it for cricket",
      },
      {
        id: "p2",
        price: 5,
        title: "Second Shoe",
        description: "I use it for football",
      },
      {
        id: "p3",
        price: 4,
        title: "Third Shoe",
        description: "I use it for cricket",
      },
      {
        id: "p2",
        price: 5,
        title: "Fourth Shoe",
        description: "I use it for badmitton",
      },
      {
        id: "p2",
        price: 5,
        title: "Fifth Shoe",
        description: "I use it for cricket",
      },
    ],
  },
  {
    fleece: [
      {
        id: "p1",
        price: 6,
        title: "First Fleece",
        description: "I use it for Outing",
      },
      {
        id: "p2",
        price: 5,
        title: "Second Fleece",
        description: "I use it for Gym",
      },
      {
        id: "p3",
        price: 4,
        title: "Third Fleece",
        description: "I use it for cricket",
      },
      {
        id: "p2",
        price: 5,
        title: "Fourth Fleece",
        description: "I use it for badmitton",
      },
      {
        id: "p2",
        price: 5,
        title: "Fifth Fleece",
        description: "I use it for cricket",
      },
    ],
  },
  {
    equipments: [
      {
        id: "p1",
        price: 6,
        title: "First Equipment",
        description: "I use it for cricket",
      },
      {
        id: "p2",
        price: 5,
        title: "Second Equipment",
        description: "I use it for football",
      },
      {
        id: "p3",
        price: 4,
        title: "Third Equipment",
        description: "I use it for cricket",
      },
      {
        id: "p2",
        price: 5,
        title: "Fourth Equipment",
        description: "I use it for badmitton",
      },
      {
        id: "p2",
        price: 5,
        title: "Fifth Equipment",
        description: "I use it for cricket",
      },
    ],
  },
];

const Products = (props) => {
  const { shoes, fleece, equipments } = DUMMY_PRODUCTS;
  return (
    <section className={classes.products}>
      {DUMMY_PRODUCTS.map((dummyProduct) => (
        <Fragment>
          <h2>
            Buy your favorite{" "}
            {utility.capitalizeWord(Object.keys(dummyProduct)[0])}
          </h2>
          <div className={classes.productLists}>
            {dummyProduct.map((product) => (
              <ProductItem
                key={product.id}
                id={product.id}
                title={product.title}
                price={product.price}
                description={product.description}
              />
            ))}
          </div>
        </Fragment>
      ))}
    </section>
  );
};

export default Products;
