import { useSelector } from "react-redux";

import Card from "../UI/Card";
import classes from "./Cart.module.css";
import CartItem from "./CartItem";

const Cart = (props) => {
  const cartItems = useSelector((state) => state.cart.items);
  const totalPrice = cartItems[0].totalPrice.toFixed(2);
  const totalDiscount = (cartItems[0].totalActualPrice - totalPrice).toFixed(2);
  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <div>
        {cartItems.map((item) => (
          <CartItem
            key={item.id}
            item={{
              id: item.id,
              title: item.name,
              quantity: item.quantity,
              total: item.totalPrice,
              price: item.price,
            }}
          />
        ))}
      </div>
      <div className={classes.price}>
        Total $ {totalPrice}
        <span className={classes.itemprice}> ( {totalDiscount} discount)</span>
      </div>
    </Card>
  );
};

export default Cart;
