      {
        id: "p3",
        price: 4,
        title: "Third Shoe",
        description: "I use it for cricket",
      },
      {
        id: "p2",
        price: 5,
        title: "Fourth Shoe",
        description: "I use it for badmitton",
      },
      {
        id: "p2",
        price: 5,
        title: "Fifth Shoe",
        description: "I use it for cricket",
      },
    ],
  },
];

const Products = (props) => {
  return (
    <section className={classes.products}>
      <h2>Buy your favorite shoes</h2>
      <div className={classes.productLists}>
        {DUMMY_PRODUCTS.map((product) => (
          <ProductItem
            key={product.id}
            id={product.id}
            title={product.title}
            price={product.price}
            description={product.description}
          />
        ))}
      </div>
    </section>
  );
};

export default Products;
